package stack_queue;import java.io.PrintStream;import java.util.NoSuchElementException;/** * Defines the methods for a Stack that handles String items */public interface StackInterface<T> {	public void push(T item);		public T pop() throws NoSuchElementException;		public T peek() throws NoSuchElementException;		public int size();		public boolean isEmpty();		public void printStack(PrintStream stream);}